styles.css:
a{
    text-decoration:none!important;

}

.div-sticky {
  position: sticky;
  top: 20px;
  bottom: 0;
}


.mobile{
display:none;
}

.desktop{
display:block;
}

header ul > li{
padding-bottom:4px;
}

header ul > li:hover{
color:#0048BF;

}

.buscador-header {
  position: relative;
}

.buscador-header::before {
  content: url('https://white-label.co/wp-content/uploads/2023/05/search.svg');
  position: absolute;
  left: 10px;
  top: 20px;
  transform: translateY(-50%);
}


/*Newsletter*/

.newsletter-sidebar {
    background: #F8FAFC;
    border: 1px solid #E2E8F0;
    padding: 15px 25px;
    border-radius: 10px;

}


.newsletter-sidebar.green-background{
    background-image: url(https://white-label.co/wp-content/uploads/2023/05/Mask-group-2.png);
    background-size: cover;
    background-repeat: no-repeat;
}

.newsletter-sidebar.green-background .title {
    font-family: Inter;
    font-size: 1.5em;
    font-weight: 500;
color:white;
text-align:center;
}

.newsletter-sidebar.green-background .subtitle{
color:white;
text-align:center;
    font-family: Inter;
font-size:18px;
}



.newsletter-sidebar form {
padding:20px;
}
.newsletter-sidebar .hs-button{
    white-space: pre-wrap;
    background: #21BE60;
    border-radius: 12px;
    padding: 10px 24px;
    border: 0;
    color: white;
    font-family: 'Inter';
    font-size: 16px;
    font-weight: 500;
    width: 100%;
}

.newsletter-sidebar.green-background .hs-button {
    background: white;
color:black;
}

.newsletter-sidebar .actions{
text-align:center;
}

.newsletter-sidebar .inputs-list {
    list-style: none;
    padding: 0;
}

.newsletter-sidebar input[type=text], .newsletter-sidebar input[type=email]
{
padding: 10px 24px;
    width: -webkit-fill-available;
    border-radius: 10px;
    border: 1px solid #CBD5E1;
}

.newsletter-sidebar .legal-consent-container .hs-dependent-field p{
    font-size: 13px;
    vertical-align: text-bottom;
    color: #94A3B8;


}

.newsletter-sidebar .legal-consent-container .hs-richtext{
    font-size: 12px;
    color: #94A3B8;
    font-weight: 400;
}

.newsletter-sidebar.green-background p {
color:white!important;
}

.newsletter-sidebar.green-background .legal-consent-container .hs-richtext {
display:none;
}

.newsletter-sidebar.green-background .hs_firstname label, .newsletter-sidebar.green-background .hs-email label {
color:white;
font-weight:600;
}

.newsletter-sidebar.footer {
    background: transparent!important;
    background-color: transparent!important;
border:0px;
padding-top:0;
}

.newsletter-sidebar.footer form{
padding-top:0;
}

.newsletter-sidebar.footer .legal-consent-container .hs-richtext {
display:none;
}

/*Se acaba el newsletter*/

.wp-block-search__input {
  padding-left: 40px; /* Ajusta este valor según el tamaño de tu imagen */
}

#social-single-top > p:nth-child(1) {

}

#highlights .ebpg-entry-wrapper{
    width: 100%;
}

.contenido-posts-home-columna2{

    display: flex;
    flex-direction: column;
    justify-content: space-between;
}

.top-posts-archive li:nth-child(n+4) {
  display: none;
}

.bottom-posts-archive li:nth-child(-n+2) {
  display: none;
}



body > div.wp-site-blocks{
padding-top:0;
padding-bottom:0;
}

#ez-toc-container {
 box-sizing: border-box;
 width: 100%!important;
 border: 0!important; 
background:#f6f7f7!important;
padding:20px!important;
}

#ez-toc-container a{
color:#64748B;
    font-family: 'Montserrat';
    line-height: 30px;
    font-weight: 500;

}

#ez-toc-container > div > span > a > label > span > svg {
  display:none;
}

#ez-toc-container > div > span > a > label {
border:0px!important;
}

#ez-toc-container > div {

    border-bottom: 1px solid #00000050;
    padding-bottom: 10px;
    margin-bottom: 10px;
}
#ez-toc-container > div > span > a::after {
  content: url("data:image/svg+xml;charset=utf-8,%3Csvg xmlns='http://www.w3.org/2000/svg' width='10' height='5' viewBox='0 0 10 5' fill='%23929292'%3E%3Cpath fill-rule='evenodd' clip-rule='evenodd' d='M9.83728 4.34476C9.62032 4.51643 9.26857 4.51643 9.05161 4.34476L5 1.13898L0.948391 4.34476C0.731434 4.51643 0.379675 4.51643 0.162718 4.34476C-0.0542402 4.1731 -0.0542401 3.89477 0.162718 3.72311L4.60716 0.2065C4.82412 0.0348351 5.17588 0.0348351 5.39284 0.2065L9.83728 3.72311C10.0542 3.89477 10.0542 4.1731 9.83728 4.34476Z'/%3E%3C/svg%3E");
  display: inline-block;
  margin-left: 5px;
  transform: rotate(180deg);
border-radius: 4px!important;

}

.taxonomy-category.wp-block-post-terms {
  white-space: nowrap;
}


.taxonomy-category a, .single-category li a  {
    color: #00235D;
    padding: 4px 8px;
    background-color: #E3F1FF;
    text-decoration: none;
border-radius:4px; 
    display: inline-block;
margin-bottom: 4px;

}

.taxonomy-category a:hover, .single-category li a:hover {
  background-color: #cbdff5;

}

.imagen-hover {
    overflow: hidden;
}

.imagen-hover img {
    -webkit-transition: all .5s ease;
    -moz-transition: all .5s ease;
    -ms-transition: all .5s ease;
    -o-transition: all .5s ease;
    transition: all .5s ease;

    vertical-align: middle;
}

.imagen-hover img:hover {
    -webkit-transform:scale(1.2); /* Safari and Chrome */
    -moz-transform:scale(1.2); /* Firefox */
    -ms-transform:scale(1.2); /* IE 9 */
    -o-transform:scale(1.2); /* Opera */
    transform:scale(1.2);
}



.hover-a:hover .shadow-hover img {
box-shadow: 7px 9px 25px 0px rgba(156,156,156,0.75);
-webkit-box-shadow: 7px 9px 25px 0px rgba(156,156,156,0.75);
-moz-box-shadow: 7px 9px 25px 0px rgba(156,156,156,0.75);
  transition: box-shadow 0.5s;
}

.hover-a:hover h2 a,.post-con-estilo:hover .wp-block-post-title a, .hover-a-posts a:hover {
  color: #0048bf;
  transition: color 0.2s;
}

.highlights .ebpg-carousel-post-holder:hover,
.highlights .ebpg-carousel-post-holder:hover .ebpg-entry-wrapper{
    background-color: white!important;
    transition: 0.1s;
}

.highlights .ebpg-carousel-post-holder:hover{
    border:2px solid #25D26A!important;
}



.single-category {

border-top:1px solid #000;
}

.single-category li {
  display: inline-block;
  margin-right: 10px; 
    margin-bottom: 10px;

}

.single-category li a {

}

#slide-archive .wp-block-post-template .wp-block-post .wp-block-columns  {
flex-direction:column;

}

/* resultados de busqueda */

.titulo-busqueda {
  display:flex;
  flex-direction:column;
}

.titulo-busqueda span{
 font-size:1.5rem;
}

.portada-author img {
    border-radius: 100%;
}

.wp-block-post-excerpt__excerpt, .montserrat{
    font-family: 'Montserrat';
}

.lexend{
font-family:'Lexend';
}

/*Autor*/

.redes-sociales .dashicons {
    width: 46px;
    height: 46px;
    font-size: 42px;
    border-radius: 4px;

}

.redes-sociales .dashicons.dashicons-linkedin::before{
    background-color: #0048BF;
    color: white;
}


.fecha-lectura, .compartir-redes-sociales p {
   color: #999999;
   font-size: 1rem;
}

.fecha-lectura > svg{
    vertical-align: text-top!important;
    margin-right: 6px;
    margin-left: 6px;
}

.compartir-redes-sociales {
   display:flex;
}

.compartir-redes-sociales .dashicons{
     margin: 0 10px;
}

.compartir-redes-sociales a {
  font-size: 24px; 
}


.compartir-redes-sociales .dashicons::before{
    font-size: 26px;
}


/*.compartir-redes-sociales .dashicons-linkedin::before{
    color: white;
    background-color:#25D26A;
}*/

.compartir-redes-sociales .dashicons-facebook, .compartir-redes-sociales .dashicons-twitter,.compartir-redes-sociales .dashicons-linkedin{
    color: #25D26A;
}

.compartir-single-caja-autor > .compartir-redes-sociales {

    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;

}

.compartir-single-caja-autor > .compartir-redes-sociales p {
font-size:18px;

}

.compartir-single-caja-autor > .compartir-redes-sociales a {
    margin: 0 10px;
}

.compartir-single-caja-autor > .compartir-redes-sociales i {
    width: 40px;
    height: 40px;
}

.compartir-single-caja-autor >.compartir-redes-sociales .dashicons::before{
    font-size: 40px;

}


#footer-cta {
  background-image: url("https://white-label.co/wp-content/uploads/2023/05/Group-33796.png")!important;
  background-repeat: no-repeat!important;
  background-size: cover!important;
}

footer > div {
 margin-bottom:0px!important;
}

footer {
overflow:hidden;
}


