#portada-archive{
  position:relative;
  overflow: hidden;

}

#bubble-archive{
position:absolute;
z-index:999;
    right: 10px;
    height: 100%;
    top: 50px;
}

#bubble-archive img {
height:100%;

}

#accordion-comments > div > h5 > a > span, #accordion-comments2 > div > h5 > a > span {
  position: relative;
  padding-left: 40px; 
}

#accordion-comments > div > h5 > a > span::after {
  content: "";
  position: absolute;
  left: 0;
  top: 50%;
  transform: translateY(-50%);
  width: 26px; 
  height: 26px; 
  background-image: url('https://white-label.co/wp-content/uploads/2023/06/pencil.svg');
  background-size: cover;
}

#accordion-comments2 > div > h5 > a > span::after {
  content: "";
  position: absolute;
  left: 0;
  top: 50%;
  transform: translateY(-50%);
  width: 30px; 
  height: 26px; 
  background-image: url('https://white-label.co/wp-content/uploads/2023/06/Vector-7.svg');
  background-size: cover;
}


#accordion-comments2 {
    border-top: 1px solid #00000040!important;
}

#accordion-comments2 .wp-block-avatar > img{
border-radius:100%;
}

.formulario-comentario .comment-reply-title{

display:none;


}

.formulario-comentario  .form-submit.wp-block-button {
text-align:right;
}

.formulario-comentario .wp-block-button__link.wp-element-button {

    background-color: #21BE60;
color:white;
    border-radius: 12px;
    text-transform: uppercase;

}

.wp-block-post-comments-form .comment-form input:not([type=submit]):not([type=checkbox]), .wp-block-post-comments-form .comment-form textarea{

border-radius: 10px;
    border: 1px solid #CBD5E1;

}


.comment-respond.formulario-comentario.wp-block-post-comments-form label[for="comment"], 
.comment-respond.formulario-comentario.wp-block-post-comments-form label[for="author"], 
.comment-respond.formulario-comentario.wp-block-post-comments-form label[for="email"], 
.comment-respond.formulario-comentario.wp-block-post-comments-form label[for="url"] {
  display: none;
}



