    width: 50px;
}

/* Estilos para pantallas móviles */
@media (max-width: 576px) {

.primera-file{
    flex-direction: column;

}

header > div > div {
    justify-content: center!important;
}

header > div > div > figure{
margin-bottom:10px!important;
}

footer .footer-second div {
     align-content: center!important;
}

footer .footer-second > div:nth-child(2) p {
text-align:center;
}

#bb-carousel-1-navigation-next, #bb-carousel-1-navigation-prev{
display:none;
}

#bubble-archive {
    z-index: 0;
}

#portada-archive > div > h1 {
z-index:10;
    font-size: 1.7rem!important;

}

#portada-archive > figure:nth-child(1) > img {
    width: 150px;


}


}


/*Responsive*/

@media (max-width: 1200px) {
  #bb-carousel-1-navigation-prev, #bb-carousel-1-navigation-next {
    display: none;
  }
}

/*Para tablets*/

@media (max-width: 768px) {
header > div > div > div {
flex-direction: column;
}
.primera-file{
  flex-direction:column;
 }
.wp-block-post-featured-image img{
    height: auto;
 }

.primera-file > :nth-child(2){
    align-self: center;

}

}

/*Para telefonos*/

@media (max-width: 500px){


.mobile{
display:block;
}

.desktop{
display:none;
}


header>div>div>div {
    flex-direction: row-reverse;
}

.compartir-redes-sociales, .fecha-lectura {
 display:flex;
    flex-direction: column;
}

.fecha-lectura svg{
    visibility: hidden;
    height: 1px;


}

.compartir-redes-sociales p {
margin:0;
}

.compartir-redes-sociales .dashicons {
    margin: 0px 6px;
}

.notas2-home {
    background: #F8FAFC;

}


}
